mapper.select:
select 

from t_settle_accounts a
where id = #{id,jdbcType=BIGINT}

mapper.update:
update t_settle_accounts set deleted  = 1 ,
  modified_date = #{modifiedDate}, modified_user_id = #{modifiedUserId}
  where id = #{id}

update t_settle_accounts set signing_status = #{signingStatus} , modified_date = #{modifiedDate},

  signing_date = #{signingDate},

modified_user_id = #{modifiedUserId} where id = #{id}

mapper.select:
select DATE_FORMAT(a.signing_date,'%Y-%m-%d') as date, ifnull(a.certified_area,0)+ifnull(a.no_register_legal_area,0)+ifnull(a.history_legacy_area,0) as area, sum_compensate as sumCompensate,ifnull(house_money, 0) as houseMoney, a.pay_total as payTotal,a.compensate_type as compensateType, ifnull(c.new_house_address,'') as newHouseName, a.swap_area1 as swapArea1,a.swap_area2 as swapArea2, a.swap_area3 as swapArea3,a.swap_area4 as swapArea4, a.swap_area5 as swapArea5 from t_settle_accounts a inner join t_area b on a.area_id = b.id and b.deleted = 0 left join t_swap_house c on c.address = a.address and c.house_owner = ifnull(a.house_owner, a.lessee) and c.deleted = 0 where a.deleted = 0 and ifnull(a.pay_total, 0) > 0
and b.project_code = #{projectCode}
and a.area_id in
#{itemId}
order by a.signing_date desc
